select 'DAY1' AS `Form`,`d1`.`recordid` AS `RecordID`,`d1`.`o_pwid` AS `o_pwid`,`d1`.`o_unit` AS `o_unit`,`d1`.`o_subunit` AS `o_subunit`,`d1`.`o_village_name` AS `o_village_name`,`d1`.`o_hamlet_name` AS `o_hamlet_name`,`d1`.`o_pw_h_name` AS `o_pw_h_name`,(case `d1`.`o_mother_status` when 1 then 'TNA' when 2 then 'PNA' when 3 then 'M_DEAD' else 'DONE' end) AS `Status`,`t2`.`DSID` AS `DSID`,`t2`.`Ph_number` AS `Ph_number`,`d1`.`Date_Created_Local` AS `Date_Created_Local` from ((`Emollient_Flat`.`Emo_Day1_Flat_5_1` `d1` join `Emollient_Device`.`DS_Tab_Details` `t2` on(((`d1`.`o_unit` = `t2`.`Unit`) and (`d1`.`o_subunit` = `t2`.`SubUnit`)))) left join `Emollient_SMS`.`Emollient_SMS_Log` `t3` on((`t3`.`RecordID` = `d1`.`recordid`))) where (isnull(`t3`.`RecordID`) and (cast(`d1`.`Date_Created_Local` as date) >= '2015-12-15'))